macro.command:
function getOrdinalSuffix(number) {
    switch (number % 10) {
        case 1:
            return `${number}st`;
        case 2:
            return `${number}nd`;
        case 3:
            return `${number}rd`;
        default:
            return `${number}th`;
    }
}

if(args[0].macroPass === "postPreambleComplete") {
    let dialogId = "deadmistlash";
    let dialogTitlePrimary = `${token.actor.name} | ${macroItem.name}`;
    let browserUser = game.gps.getBrowserUser({ actorUuid: actor.uuid });
    let gmUser = game.gps.getPrimaryGM();
    const optionBackground = (document.body.classList.contains("theme-dark")) ? 'black' : 'var(--color-bg)';
    let result;


    let castLevel = workflow.castData.castLevel;

    let optionsContent = "";
    for (let upcastValue = 1; upcastValue <= 3; upcastValue++) {
        let newCastLevel = castLevel + upcastValue;
        if (newCastLevel > 9) break;

        let selfDamageTotal = upcastValue * 5;
        optionsContent += `
            <option name="${macroItem.img}" value="${upcastValue}" class="gps-dialog-option" style="background-color: ${optionBackground};">
                +${upcastValue} (Cast Level: ${newCastLevel} | Self Damage: ${selfDamageTotal})
            </option>
        `;
    }

    let dialogContent = `
        <div class="gps-dialog-container">
            <div class="gps-dialog-section">
                <div class="gps-dialog-content">
                    <p class="gps-dialog-paragraph">Would you like to consume some of your life force to empower this spell and cast it at a higher level?</p>
                    <div>
                        <div class="gps-dialog-flex">
                            <label for="item-select_${dialogId}" class="gps-dialog-label">Level:</label>
                            <select id="item-select_${dialogId}" class="gps-dialog-select">
                                ${optionsContent}
                            </select>
                            <div id="image-container" class="gps-dialog-image-container">
                                <img id="img_${dialogId}" src="${macroItem.img}" class="gps-dialog-image">
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div class="gps-dialog-button-container">
                <button id="pauseButton_${dialogId}" type="button" class="gps-dialog-button">
                    <i class="fas fa-pause" id="pauseIcon_${dialogId}" style="margin-right: 5px;"></i>Pause
                </button>
            </div>
        </div>
    `;

    result = await game.gps.socket.executeAsUser("process3rdPartyReactionDialog", browserUser, {dialogTitle:dialogTitlePrimary,dialogContent,dialogId,initialTimeLeft: 30,validTokenPrimaryUuid: token.document.uuid,source:gmUser === browserUser ? "gm" : "user",type:"singleDialog"});

    const { userDecision, selectedItemUuid } = result;

    if (!userDecision) {
        return;
    }
    else if (userDecision) {
        let selfDamage;
        (+selectedItemUuid === 1) ? selfDamage = 5 : (+selectedItemUuid === 2) ? selfDamage = 10 : selfDamage = 15;

        let selfDamageRoll = await new CONFIG.Dice.DamageRoll(`${selfDamage}`).evaluate();

        const itemDataChat = {
            name: "Dead Mist Lash - Damage (Self)",
            type: "feat",
            img: macroItem.img
        }

        new MidiQOL.DamageOnlyWorkflow(actor, token, selfDamageRoll.total, "none", token ? [token] : [], selfDamageRoll, {itemData: itemDataChat});

        let newCastLevel = +selectedItemUuid + +castLevel;
        await game.messages.get(workflow.itemCardId).setFlag('dnd5e','use.spellLevel', newCastLevel);
        workflow.castData.castLevel = newCastLevel;
    }
}
