macro.command:
if(args[0] === "on") {
    let effectData = token.actor.appliedEffects.find(e => e.name === item.name);
    if(!effectData) return;
    let effectOriginToken = await fromUuid(effectData.getFlag("gambits-premades", "festeringFeverTokenUuid"));
    if(!effectOriginToken) return;
    let castTotal = await effectData.getFlag("gambits-premades", "festeringFeverCastTotal");
    if(!castTotal) return;
    let spellDC = await effectData.getFlag("gambits-premades", "festeringFeverSpellDC");
    await actor.setFlag("midi-qol", "festeringFeverApplied", true);

    const itemData = {
        name: item.name,
        img: item.img,
        type: "feat",
        effects: [],
        flags: {
            "midi-qol": {
                noProvokeReaction: true,
                onUseMacroName: null,
                forceCEOff: true
            },"midiProperties": {
                saveDamage: "halfdam",
                magiceffect: true,
                magicdamage: true
            },
            "autoanimations": {
                killAnim: true
            }
        },
        system: {
            equipped: true,
            actionType: "save",
            save: { dc: spellDC, ability: "con", scaling: "flat" },
            damage: { parts: [[`${castTotal}d8`, "necrotic"]] },
            components: { concentration: false, material: false, ritual: false, somatic: false, value: "", vocal: false },
            duration: { units: "inst", value: undefined }
        }
    };

    const itemUpdate = new CONFIG.Item.documentClass(itemData, { parent: effectOriginToken.actor });
    const options = { showFullCard: false, createWorkflow: true, versatile: false, configureDialog: false, targetUuids: [token.document.uuid], workflowOptions: {autoRollDamage: 'always', autoFastDamage: true} };
    const saveResult = await MidiQOL.completeItemUse(itemUpdate, {}, options);

    let content;

    if (saveResult.failedSaves.size === 0 || castTotal === 1) {
        if(saveResult.failedSaves.size === 0) {
            content = `<span style='text-wrap: wrap;'>The target saves for half damage and the fever subsides.<br><img src="${token.actor.img}" width="30" height="30" style="border:0px"></span>`;
        }
        else if(castTotal === 1) {
            content = `<span style='text-wrap: wrap;'>The target does not save but the fever subsides as its energy runs out.<br><img src="${token.actor.img}" width="30" height="30" style="border:0px"></span>`;
        }
        await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: token.actor.uuid, effects: [effectData.id] });

        let targets = canvas.tokens.placeables.filter(t => {
            if (t.document.disposition !== token.document.disposition) return;
            if (!t.actor.flags["gambits-premades"].festeringFeverApplied) return;
            return t;
        });

        if (targets.length === 0) return;

        for(let target of targets) {
            await target.actor.unsetFlag("midi-qol", "festeringFeverApplied");
        }
    }
    else {
        content = `<span style='text-wrap: wrap;'>The target does not save and the fever continues to fester.<br><img src="${token.actor.img}" width="30" height="30" style="border:0px"></span>`;
    }

    new Sequence()

    .effect()
    .file("jb2a.magic_signs.rune.necromancy.complete.green")
    .attachTo(token)
    .scaleToObject(2.5)
    .opacity(1)
    .zIndex(1)
    .belowTokens(false)
    .fadeIn(1500)
    .fadeOut(1500)
    .filter("ColorMatrix", { hue: 140, brightness: 0.8, contrast: 1, saturate: 1 })

    .play()

    let actorPlayer = MidiQOL.playerForActor(effectOriginToken.actor);

    let chatData = {
        user: actorPlayer.id,
        speaker: ChatMessage.getSpeaker({ token: effectOriginToken }),
        content: game.i18n.localize(content)
    };
    ChatMessage.create(chatData);
}

else if(args[0].macroPass === "postActiveEffects") {
    let effectData = target.actor.appliedEffects.find(e => e.name === macroItem.name);
    let castTotal = workflow.castData.castLevel;
    let spellDC = actor.system.attributes.spelldc;
    await effectData.setFlag("gambits-premades", "festeringFeverCastTotal", castTotal);
    await effectData.setFlag("gambits-premades", "festeringFeverSpellDC", spellDC);
    await effectData.setFlag("gambits-premades", "festeringFeverTokenUuid", token.document.uuid);
}

else if(args[0] === "each") {
    let effectData = token.actor.appliedEffects.find(e => e.name === item.name);
    if(!effectData) return;
    let effectOriginToken = await fromUuid(effectData.getFlag("gambits-premades", "festeringFeverTokenUuid"));
    if(!effectOriginToken) return;
    let castTotal = await effectData.getFlag("gambits-premades", "festeringFeverCastTotal");
    if(!castTotal) return;

    castTotal = castTotal - 1;
    await effectData.setFlag("midi-qol", "festeringFeverCastTotal", castTotal);

    const rangeCheck = MidiQOL.findNearby(null, token, 10, { includeToken: false });
    const withinRange = rangeCheck.filter(t => (!t.actor.flags["gambits-premades"].festeringFeverApplied) && t.document.disposition === token.document.disposition);

    if (withinRange.length > 0) {
        const randomToken = withinRange[Math.floor(Math.random() * withinRange.length)];
        await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: randomToken.actor.uuid, effects: [effectData.toObject()] });
    }
    else {
        let targets = canvas.tokens.placeables.filter(t => {
            if (t.document.disposition !== token.document.disposition) return;
            if (!t.actor.flags["gambits-premades"].festeringFeverApplied) return;
            return t;
        });

        if (targets.length === 0) return;

        for(let target of targets) {
            await target.actor.unsetFlag("midi-qol", "festeringFeverApplied");
        }

        let content = `<span style='text-wrap: wrap;'>The fever subsides on the target and there are no additional targets within 10 feet.<br><img src="${token.actor.img}" width="30" height="30" style="border:0px"></span>`;

        let actorPlayer = MidiQOL.playerForActor(effectOriginToken.actor);

        let chatData = {
            user: actorPlayer.id,
            speaker: ChatMessage.getSpeaker({ token: effectOriginToken }),
            content: game.i18n.localize(content)
        };
        ChatMessage.create(chatData);
    }

    await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: target.actor.uuid, effects: [effectData.id] });
}
