macro.command:
if(args[0].macroPass === "isDamaged") {
    let itemName = macroItem.name;
    let target = workflow.token;
    let gpsUuid = "7c352bfb-613c-46d7-91df-ac3d3a50c0c5";

    let findValidToken = game.gps.findValidToken({initiatingTokenUuid: target.document.uuid, targetedTokenUuid: token.document.uuid, itemName: itemName, itemType: "feature", itemChecked: ["channel divinity"], reactionCheck: true, sightCheck: false, rangeCheck: true, rangeTotal: 30, dispositionCheck: true, dispositionCheckType: "enemy", workflowType: "attack", workflowCombat: "true", gpsUuid: gpsUuid});

    if(!findValidToken) return;

    let dialogId = gpsUuid;

    let initialTimeLeft = 15;
    const dialogTitlePrimary = `${actor.name} | ${macroItem.name}`;
    const dialogTitleGM = `Waiting for ${actor.name}'s selection | ${macroItem.name}`;
    let browserUser = game.gps.getBrowserUser({ actorUuid: actor.uuid });
    let gmUser = game.gps.getPrimaryGM();
    let result;

    let dialogContent = `
		<div class="gps-dialog-container">
			<div class="gps-dialog-section">
				<div class="gps-dialog-content">
					<div>
						<div class="gps-dialog-flex">
							<p class="gps-dialog-paragraph">You have been damaged, would you like to use ${macroItem.name} to inflict cold damage on the attacker?</p>
							<div id="image-container" class="gps-dialog-image-container">
								<img id="img_${dialogId}" src="${macroItem.img}" class="gps-dialog-image">
							</div>
						</div>
					</div>
				</div>
			</div>
			<div class="gps-dialog-button-container">
				<button id="pauseButton_${dialogId}" type="button" class="gps-dialog-button">
					<i class="fas fa-pause" id="pauseIcon_${dialogId}" style="margin-right: 5px;"></i>Pause
				</button>
			</div>
		</div>
	`;

    if (MidiQOL.safeGetGameSetting('gambits-premades', 'Mirror 3rd Party Dialog for GMs') && browserUser !== gmUser) {
    let userDialogArgs = { dialogTitle:dialogTitlePrimary,dialogContent,dialogId,initialTimeLeft,validTokenPrimaryUuid: token.document.uuid,source: "user",type: "multiDialog", browserUser: browserUser };

    let gmDialogArgs = { dialogTitle:dialogTitleGM,dialogContent,dialogId,initialTimeLeft,validTokenPrimaryUuid: token.document.uuid,source: "gm",type: "multiDialog" };

    result = await game.gps.socket.executeAsUser("handleDialogPromises", gmUser, {userDialogArgs, gmDialogArgs});
} else {
    result = await game.gps.socket.executeAsUser("process3rdPartyReactionDialog", browserUser, {dialogTitle:dialogTitlePrimary,dialogContent,dialogId,initialTimeLeft,validTokenPrimaryUuid: token.document.uuid,source:gmUser === browserUser ? "gm" : "user",type:"singleDialog"});
    }

	const { userDecision, enemyTokenUuid, allyTokenUuid, damageChosen, abilityCheck, source, type } = result;

    if (userDecision) {
        let activityToUpdate = macroItem.system.activities.find(a => a.identifier === "syntheticSave");
        let damageParts = foundry.utils.duplicate(activityToUpdate.damage.parts);
        damageParts[0].custom.formula = target.actor.system.details.type.value === "undead" ? `1d6 + ${workflow.damageTotal}` : workflow.damageTotal

        await game.gps.socket.executeAsUser("gpsActivityUpdate", gmUser, { activityUuid: activityToUpdate.uuid, updates: {"damage.parts": damageParts} });
        await game.gps.gpsActivityUse({itemUuid: macroItem.uuid, identifier: "syntheticSave", targetUuid: target.document.uuid});

        let cprConfig = game.gps.getCprConfig({itemUuid: macroItem.uuid});
        const { animEnabled } = cprConfig;
        if(animEnabled) {
            new Sequence()
                .effect()
                    .file("animated-spell-effects-cartoon.fire.118")
                    .fadeIn(500)
                    .fadeOut(500)
                    .atLocation(target, {offset:{x: 0, y: -10}})
                    .scaleToObject(3)
                    .filter("ColorMatrix", { brightness: 0, contrast: 1 })
                .play()
        }
    }
}
